engine=pyttsx3.init()
def speak(audio):
    print(audio)
    engine.say(audio)
    engine.runAndWait()
    engine.stop()

speak('How may I help you!')
query=takecommand().lower()
if('say 'in query):
    speak(query.replace('say ','saying '))
elif('quote'in query):
    speak(quotes())
elif('conn'in query):
    os.system('netsh interface set interface name=Wi-Fi admin = ENABLED')
elif('dconn'in query):
    os.system('netsh interface set interface name=Wi-Fi admin = DISABLED')
elif('coin'in query)and(random.randrange(100)>=50):
    speak('HEADS')
elif('coin'in query):
    speak('TAILS')
elif('wikipedia 'in query):
    command='https://en.wikipedia.org/'+query.replace('wiki ','wiki/')
    threading._start_new_thread(search)
elif('youtube 'in query):
    command='https://www.youtube.com/results?'+query.replace('youtube ','search_query=')
    threading._start_new_thread(search)
elif('+'in query)or('-'in query)or('*'in query)or('/'in query):
    query=query.replace('+','plus')
    query=query.replace('-','minus')
    query=query.replace('*','times')
    query=query.replace('/','devide by')
    command='https://www.bing.com/search?q='+query
    threading._start_new_thread(search)
else:
    command='https://www.bing.com/search?q='+query
    threading._start_new_thread(search)
